import React, { useState } from 'react';
import Layout from "../components/layout/Layout";
import Router from "next/router";
import { Formulario, Campo, InputSubmit, Titulo, Error} from "../components/ui/Formulario";

//Importando el firebase para iniciar la conexion 
import firebase from "../firebase";

//Utilizando el hooks validacion
import useValidacion from "../hooks/useValidacion";
import validarCrearCuenta from "../validacion/validarCrearCuenta";

const STATE_INICIAL = {
    nombre: "",
    email: "",
    password: ""
}

const NuevoProducto = () => {

    //Error al crear cuenta
    const [error, guardarError] = useState(false);

    const { valores, errores, submitForm, handleSubmit, handleChange, handleBlur } = useValidacion(STATE_INICIAL, validarCrearCuenta, crearCuenta);

    const {nombre, email, password} = valores;
    
    async function crearCuenta(){
        //console.log("Creando cuenta...");
        try {
            await firebase.registrar(nombre, email, password);   
            Router.push("/");
        } catch (error) {
            console.error("Hubo un error al crear el usuario " , error.message);
            guardarError(error.message);
        }
    }

    return ( 
        <Layout>
            <Titulo>Nuevo Producto</Titulo>
            <Formulario
                onSubmit = {handleSubmit}
                noValidate
            >
                <Campo>
                    <label htmlFor="nombre">Nombre</label>
                    <input
                        type="text"
                        id="nombre"
                        placeholder="Tu Nombre"
                        name="nombre"
                        onChange={handleChange}
                        onBlur = {handleBlur}
                        value={nombre}
                    />
                </Campo>
                {errores.nombre && <Error>{errores.nombre}</Error>}
                <Campo>
                    <label htmlFor="empresa">Empresa: </label>
                    <input
                        type="text"
                        id="empresa"
                        placeholder="Empresa"
                        name="empresa"
                        onChange={handleChange}
                        onBlur = {handleBlur}
                        value={empresa}
                    />
                </Campo>
                {errores.empresa && <Error>{errores.empresa}</Error>}
                <Campo>
                    <label htmlFor="imagen">Imagen</label>
                    <input
                        type="file"
                        id="imagen"
                        name="imagen"
                        onChange={handleChange}
                        onBlur = {handleBlur}
                        value={imagen}
                    />
                </Campo>
                {errores.imagen && <Error>{errores.imagen}</Error>}
                <Campo>
                    <label htmlFor="url">URL</label>
                    <input
                        type="url"
                        id="url"
                        name="url"
                        onChange={handleChange}
                        onBlur = {handleBlur}
                        value={url}
                    />
                </Campo>
                {errores.url && <Error>{errores.url}</Error>}

                {error && <Error>{error}</Error>}
                <InputSubmit
                    type="submit"
                    value="Crear Cuenta"
                />
            </Formulario>
        </Layout>
     );
}
export default NuevoProducto;
