import styled from "@emotion/styled";

export const Formulario = styled.form`
    max-width: 600px;
    width: 95%;
    margin: 5rem auto 0 auto;
`;

export const Campo = styled.div`
        margin-bottom: 2rem;
        display: flex;
        align-items: center;

        label{
            flex: 0 0 150px;
            font-size: 1.8rem;
        }

        input{
            flex: 1;
            padding: 1rem;
        }
`;
